const ndLog = require('./index');

test('logs INFO message', () => {
    expect(ndLog('INFO', 'Yeah this is just some info')).toBe(true);
})

test('logs WARNING message', () => {
    expect(ndLog('WARNING', 'A warning in a log')).toBe(true);
})

test('logs ERROR message', () => {
    expect(ndLog('ERROR', 'This is an ERROR')).toBe(true);
})

test('logs OTHER message', () => {
    expect(ndLog('OTHER', 'This is other type message')).toBe(true);
})
