const fs = require('fs');

const simpleLogger = (type: string, message: string) => {
    try {
        // message preparation
        const timestamp = new Date().toISOString();
        const completeMessage = `\n${timestamp} | ${type} | ${message}`;

        // write to file
        fs.appendFile('log.txt', completeMessage, (err) => {
            if (err) throw err;
        });

        return true;
    } catch (err) {
        return err;
    }
}

module.exports = simpleLogger;